# Generate all 24 videos in parallel (8 at a time)
for i in {0..23}; do
    scene=$((i + 1))
    scene_num=$(printf "%02d" $scene)
    prompt="${MOBILE_PROMPTS[$i]}"
    
    (
        echo "ğŸ“± Scene $scene_num: Generating mobile video (9:16)..."
        
        response=$(curl -s -X POST "$VIDEO_ENDPOINT" \
            -H "Authorization: Key $FAL_API_KEY" \
            -H "Content-Type: application/json" \
            -d "{
                \"prompt\": \"$prompt\",
                \"duration\": 8,
                \"aspect_ratio\": \"9:16\",
                \"resolution\": \"1080p\",
                \"seed\": $SEED
            }")
        
        video_url=$(echo "$response" | jq -r '.video.url // empty')
        
        if [[ -n "$video_url" ]]; then
            curl -s -o "mobile/scene${scene_num}.mp4" "$video_url"
            if [[ -f "mobile/scene${scene_num}.mp4" ]]; then
                size=$(ls -lh "mobile/scene${scene_num}.mp4" | awk '{print $5}')
                echo "   âœ… Scene $scene_num: Mobile video saved ($size)"
            fi
        else
            echo "   âŒ Scene $scene_num: Failed"
            echo "$response" > "responses/scene${scene_num}_mobile_error.json"
        fi
    ) &
    
    # Limit to 8 concurrent jobs
    if (( (scene % 8) == 0 )); then
        wait
    fi
    
    sleep 2
done

